from itertools import permutations

dna = ''
print(dna.find('AUG'))

codes = list(permutations(['00', '01', '10','11'], 4))
for code in codes:
	mapping = {'A': code[0], 'T': code[1], 'C': code[2], 'G': code[3]}

	dna_bin = ''
	for c in dna:
		dna_bin += mapping[c]
	
	print(dna_bin)
